package conditional_statement;

public class Largest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int A=1,B=12,C=6;
     if(A>=B && A>=C)
     {
    	System.out.println("A"); 
     }
     else if(B>=C)
     {
    	System.out.println("B"); 
     }
     else
     {
    	 System.out.println("C");
     }
     }

}
